/**
 * You are given two strings s and t.
 *
 * String t is generated by random shuffling string s and then add one more
 * letter at a random position.
 *
 * Return the letter that was added to t.
 *
 * Constraints:
 *    0 <= s.length <= 1000
 *    t.length == s.length + 1
 *    s and t consist of lowercase English letters.
 */

/**
 * @param {string} s
 * @param {string} t
 * @return {character}
 */
const findTheDifference1 = (s, t) => {
  for (let i = 0; i < t.length; i++) {
    const pos = s.indexOf(t[i])
    if (pos === -1) return t[i]
    s = s.slice(0, pos) + s.slice(pos + 1)
  }
}

const findTheDifference2 = (s, t) =>
  [...t].sort().find((c, i) => c !== [...s].sort()[i])

const findTheDifference3 = (s, t) => {
  s = [...s].sort()
  return [...t].sort().find((c, i) => c !== s[i])
}

const findTheDifference4 = (s, t) => {
  s = s.split('').sort()
  return t
    .split('')
    .sort()
    .find((c, i) => c !== s[i])
}

const findTheDifference5 = (s, t) => {
  let sum = 0,
    i = 0
  for (; i < s.length; i++) {
    sum += t.charCodeAt(i)
    sum -= s.charCodeAt(i)
  }
  sum += t.charCodeAt(i)
  return String.fromCharCode(sum)
}

const findTheDifference = (s, t) => {
  let sum = 0,
    i = 0
  for (; i < s.length; i++) {
    sum ^= t.charCodeAt(i) ^ s.charCodeAt(i)
  }
  sum ^= t.charCodeAt(i)
  return String.fromCharCode(sum)
}

// prettier-ignore
const funcs = [
  findTheDifference,
]

const data = [
  ['abcd', 'abcde', 'e'],
  ['', 'y', 'y'],
]

for (const func of funcs) {
  for (const [s, t, expected] of data) {
    console.log(func(s, t) === expected)
  }
}
